scan_script_only_check.script:
(function (engine) {

    var _objUpdateVersionAPI = new global.UpdateVersionAPI();
    var _strPropertyName     = 'my_instance_scan_collection.table_exclusion_list';
    var _strExcludedTables   = String(gs.getProperty(_strPropertyName, '')).trim();
    var _arrExcludedTables   = _strExcludedTables.split(',');
    var _arrScannedTables    = [
        {table: 'sys_script',            field: 'script'},           // Business Rules
		{table: 'sys_script_include',    field: 'script'},           // Script Includes
		{table: 'sys_transform_map',     field: 'script'},           // Transform Scripts
		{table: 'sys_data_source',       field: 'script'},           // Data Source scripts
		{table: 'sys_script_client',     field: 'script'},           // Client Scripts
		{table: 'catalog_script_client', field: 'script'},           // Catalog Client Scripts
		{table: 'sys_ui_script',         field: 'script'},           // UI Scripts
		{table: 'sys_ui_action',         field: 'script'},           // UI Actions
		{table: 'sys_ui_policy',         field: 'script'},           // UI Policies
		{table: 'sys_ui_context_menu',   field: 'action_script'},    // Context Menu Scripts
		{table: 'wf_activity',           field: 'script'},           // Workflow Activities
		{table: 'sys_flow_context',      field: 'variables'},        // Flow variables
		{table: 'sc_cat_item',           field: 'script'},           // Catalog Item scripts
		{table: 'sc_cat_item_producer',  field: 'producer_script'},  // Record Producer scripts
		{table: 'sp_widget',             field: 'script'},           // Service Portal Widgets
		{table: 'sp_instance',           field: 'script'},           // Portal Page Instances
		{table: 'sys_script_email',      field: 'script'},           // Email Scripts
		{table: 'sys_trigger',           field: 'script'},           // Scheduled Jobs
		{table: 'sysauto_script',        field: 'script'},           // Auto Script
    ];

    // Regex pattern for detecting 32-character sys_id-like strings
    var _sysIdPattern = /[a-f0-9]{32}/;

    for (var i = 0; i < _arrScannedTables.length; i++) {
        var _table = _arrScannedTables[i].table.trim();
        var _field = _arrScannedTables[i].field.trim();

        if (_arrExcludedTables.indexOf(_table) === -1) {
            var _gr = new GlideRecord(_table);
            _gr.query();

            if (_gr.hasNext()) {
                var _arrCustomSysIds = _objUpdateVersionAPI.getCustomerFileIds(_gr);
                for (var j = 0; j < _arrCustomSysIds.length; j++) {
                    if (_gr.get(_arrCustomSysIds[j])) {
                        var _scriptContent = _gr.getValue(_field);
                        if (_scriptContent && _sysIdPattern.test(_scriptContent)) {
                            engine.finding.setCurrentSource(_gr);
                            engine.finding.increment();
                        }
                    }
                }
            }
        }
    }

})(engine);
