sys_variable_value.value:
(function execute(inputs, outputs) {
    var updateXml = new GlideRecord('sys_update_xml');
    updateXml.addEncodedQuery('update_setISNOTEMPTY^nameLIKE' + inputs.sourceRecord);
    updateXml.orderByDesc('sys_updated_on');
    updateXml.query();
    if (updateXml.next()) {
        outputs.updatesetname = updateXml.update_set.getDisplayValue() + '[' + updateXml.getValue('update_set') + ']';
    }
})(inputs, outputs);

