sys_variable_value.value:
(function execute(inputs, outputs) {
    var recordGr = new GlideRecord(inputs.tableName);
    if (recordGr.get(inputs.sourceRecord) && inputs.updateSetName) {
        if (inputs.tableName == 'sys_ui_action') {
            recordGr.comments = 'Created in ' + inputs.updateSetName;
        } else {
            recordGr.description = 'Created in ' + inputs.updateSetName;
        }
        recordGr.setWorkflow(false);
        recordGr.autoSysFields(false);
        recordGr.update();
    }
})(inputs, outputs);

